import {Component, OnInit} from '@angular/core';
import {FormBuilder, FormArray, Validators, FormGroup} from '@angular/forms';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import {subjectValidator} from 'src/validators/subject-validator';
import {degreeValidator} from 'src/validators/degree-validator';
import {requiredFieldValidator} from 'src/validators/required-field-validator';
import {dateValidator} from 'src/validators/date-validator';
import {emailValidator} from 'src/validators/email-validator';
import {passwordValidator} from 'src/validators/password-validator';
import {templateIdValidator} from 'src/validators/template-id-validator';
import {graduationYearValidator} from 'src/validators/graduation-year-validator';
import {HttpClient} from '@angular/common/http';


@Component({
    selector: 'app-main',
    templateUrl: './main.component.html',
    styleUrls: ['./main.component.css']
})
export class MainComponent implements OnInit {

    mainForm: FormGroup;
    selectedFile: File | null = null;

    constructor(private fb: FormBuilder, private http: HttpClient) {
        this.mainForm = this.fb.group({
            full_name: ['', [Validators.required, Validators.minLength(3)]],
            date_of_birth: ['', [Validators.required]],
            mail: ['', [Validators.required, Validators.email]],
            marital_status: [''],
            hobbies: [''],
            dream_sector1: [''],
            dream_sector2: [''],
            career_plans: [''],
            additional_info: [''],
            minor_course_details: [''],
            skills: [''],
            transaction_id: [''],
            prof_summary: [''],
            // password: ['', [Validators.required, Validators.minLength(6)]],
            // template_id: [1, [Validators.required]],
            languages: this.fb.array([this.createLanguage()]),
            education: this.fb.array([this.createEducation()]),
            projects: this.fb.array([this.createProject()]),
            social_accounts: this.fb.array([this.createSocialAccount()]),
            work_experience: this.fb.array([this.createWorkExperience()]),
            internships: this.fb.array([this.createInternship()]),
            volunteer_activities: this.fb.array([this.createVolunteerActivity()]),
            accomplishments: this.fb.array([this.createAccomplishment()]),
            positions_of_responsibility: this.fb.array([this.createPositionOfResponsibility()]),
            extra_curriculars: this.fb.array([this.createExtraCurricular()]),
            //  documents: this.fb.array([this.createDocument()])
            certifications: this.fb.array([this.createCertification()]),
            competitions: this.fb.array([this.createCompetition()]),
            conferences_workshops: this.fb.array([this.createConferenceWorkshop()]),
            test_scores: this.fb.array([this.createTestScore()]),
            patents_publications: this.fb.array([this.createPatentPublication()]),
            scholarships: this.fb.array([this.createScholarship()]),
            photograph: ['']
        });
    }

    ngOnInit(): void {
    }

    // Getter methods for form arrays
    get languagesFormArray() {
        return this.mainForm.get('languages') as FormArray;
    }

    get educationFormArray() {
        return this.mainForm.get('education') as FormArray;
    }

    get projectsFormArray() {
        return this.mainForm.get('projects') as FormArray;
    }

    get socialAccountsFormArray() {
        return this.mainForm.get('social_accounts') as FormArray;
    }

    get workExperienceFormArray() {
        return this.mainForm.get('work_experience') as FormArray;
    }

    get internshipsFormArray() {
        return this.mainForm.get('internships') as FormArray;
    }

    get volunteerActivitiesFormArray() {
        return this.mainForm.get('volunteer_activities') as FormArray;
    }

    get accomplishmentsFormArray() {
        return this.mainForm.get('accomplishments') as FormArray;
    }

    get positionsOfResponsibilityFormArray() {
        return this.mainForm.get('positions_of_responsibility') as FormArray;
    }

    get extraCurricularsFormArray() {
        return this.mainForm.get('extra_curriculars') as FormArray;
    }

    //get documentsFormArray() { return this.mainForm.get('documents') as FormArray; }
    get certificationsFormArray() {
        return this.mainForm.get('certifications') as FormArray;
    }

    get competitionsFormArray() {
        return this.mainForm.get('competitions') as FormArray;
    }

    get conferencesWorkshopsFormArray() {
        return this.mainForm.get('conferences_workshops') as FormArray;
    }

    get testScoresFormArray() {
        return this.mainForm.get('test_scores') as FormArray;
    }

    get patentsPublicationsFormArray() {
        return this.mainForm.get('patents_publications') as FormArray;
    }

    get scholarshipsFormArray() {
        return this.mainForm.get('scholarships') as FormArray;
    }

    // Methods to add new form groups to form arrays
    addLanguage() {
        this.languagesFormArray.push(this.createLanguage());
    }

    addEducation() {
        this.educationFormArray.push(this.createEducation());
    }

    addProject() {
        this.projectsFormArray.push(this.createProject());
    }

    addSocialAccount() {
        this.socialAccountsFormArray.push(this.createSocialAccount());
    }

    addWorkExperience() {
        this.workExperienceFormArray.push(this.createWorkExperience());
    }

    addInternship() {
        this.internshipsFormArray.push(this.createInternship());
    }

    addVolunteerActivity() {
        this.volunteerActivitiesFormArray.push(this.createVolunteerActivity());
    }

    addAccomplishment() {
        this.accomplishmentsFormArray.push(this.createAccomplishment());
    }

    addPositionOfResponsibility() {
        this.positionsOfResponsibilityFormArray.push(this.createPositionOfResponsibility());
    }

    addExtraCurricular() {
        this.extraCurricularsFormArray.push(this.createExtraCurricular());
    }

    //addDocument() { this.documentsFormArray.push(this.createDocument()); }
    addCertification() {
        this.certificationsFormArray.push(this.createCertification());
    }

    addCompetition() {
        this.competitionsFormArray.push(this.createCompetition());
    }

    addConferenceWorkshop() {
        this.conferencesWorkshopsFormArray.push(this.createConferenceWorkshop());
    }

    addTestScore() {
        this.testScoresFormArray.push(this.createTestScore());
    }

    addPatentPublication() {
        this.patentsPublicationsFormArray.push(this.createPatentPublication());
    }

    addScholarship() {
        this.scholarshipsFormArray.push(this.createScholarship());
    }

    // Methods to create form groups
    createLanguage(): FormGroup {
        return this.fb.group({
            language: [''],
            proficiency: ['']
        });
    }

    createEducation(): FormGroup {
        return this.fb.group({
            grade_year: [''],
            grad_year: [''],
            percentage_cgpa: [''],
            specialization: [''],
            institution: ['']
        });
    }

    createProject(): FormGroup {
        return this.fb.group({
            title: [''],
            domain: [''],
            duration: [''],
            description: [''],
            url: ['']
        });
    }

    createSocialAccount(): FormGroup {
        return this.fb.group({
            name: [''],
            url: ['']
        });
    }

    createWorkExperience(): FormGroup {
        return this.fb.group({
            role: [''],
            cause: [''],
            description: [''],
            organization: ['']
        });
    }

    createInternship(): FormGroup {
        return this.fb.group({
            role: [''],
            cause: [''],
            description: [''],
            organization: ['']
        });
    }

    createVolunteerActivity(): FormGroup {
        return this.fb.group({
            role: [''],
            cause: [''],
            description: [''],
            organization: ['']
        });
    }

    createAccomplishment(): FormGroup {
        return this.fb.group({
            name: [''],
            description: [''],
            date: [''],
            //  type: [''],
            organization: ['']
        });
    }

    createPositionOfResponsibility(): FormGroup {
        return this.fb.group({
            role: [''],
            cause: [''],
            description: [''],
            organization: ['']
        });
    }

    createExtraCurricular(): FormGroup {
        return this.fb.group({
            name: [''],
            category: [''],
            description: ['']
        });
    }

    // createDocument(): FormGroup {
    //   return this.fb.group({
    //     name: [''],
    //     category: [''],
    //     description: ['']
    //   });
    // }

    createCertification(): FormGroup {
        return this.fb.group({
            name: [''],
            date: [''],
            organization: ['']
        });
    }

    createCompetition(): FormGroup {
        return this.fb.group({
            name: [''],
            date: [''],
            position: [''],
            organization: ['']
        });
    }

    createConferenceWorkshop(): FormGroup {
        return this.fb.group({
            name: [''],
            date: [''],
            description: [''],
            organization: ['']
        });
    }

    createTestScore(): FormGroup {
        return this.fb.group({
            name: [''],
            date: [''],
            score: ['']
        });
    }

    createPatentPublication(): FormGroup {
        return this.fb.group({
            name: [''],
            date: [''],
            description: [''],
            organization: ['']
        });
    }

    createScholarship(): FormGroup {
        return this.fb.group({
            name: [''],
            date: [''],
            description: [''],
            organization: ['']
        });
    }

    onFileSelected(event: any): void {
        const file = event.target.files[0];
        if (file && (file.type === 'image/jpeg' || file.type === 'image/jpg')) {
            this.selectedFile = file;

        } else {
            alert('Please select a valid .jpg or .jpeg file.');
            this.selectedFile = null;

        }
    }

    onSubmit(): void {
        if (this.mainForm.valid) {
            const formData = new FormData();

            // Append form data
            for (const key in this.mainForm.value) {
                formData.append(key, this.mainForm.value[key]);
            }

            // Append the selected file
            if (this.selectedFile) {
                formData.append('photograph', this.selectedFile, this.selectedFile.name);
            }

            console.log('FormData:', formData);

            // Send formData to database
            this.http.post('http://127.0.0.1:5000/user/add_full', formData, {responseType: 'text'})
                .subscribe(
                    (response) => {
                        console.log('Form Submitted Successfully:', response);
                    },
                    (error) => {
                        console.error('Error updating Database:', error);
                    }
                );

            // Send formData to your server
            this.http.post('http://127.0.0.1:5000/static', formData, {responseType: 'text'})
                .subscribe((response) => {
                        console.log('Form Submitted');
                        const newWindow = window.open();
                        if (newWindow) {
                            newWindow.document.write(response);
                            newWindow.document.close();
                        } else {
                            // console.log(response);
                            console.error('Failed to open new window');
                        }
                    }, (error) => {
                        console.error('Error submitting form:', error);
                    }
                );
        } else {
            console.log('Form is invalid');
        }
    }
}
<div class="container-fluid d-flex justify-content-center align-items-center" id="htmlData">
    <div class="w-100">
      <form [formGroup]="mainForm" (ngSubmit)="onSubmit()">
        <!-- Personal Details Section -->
        <div class="mt-3">
          <h5 class="mb-2 btn btn-dark">Personal Details</h5>
          <div class="d-flex gap-0 row">
            <div class="form-group col-12 col-md-4 mt-2">
              <input type="text" class="form-control" formControlName="full_name" placeholder="Full Name" [class.is-invalid]="mainForm.get('full_name')?.invalid && mainForm.get('full_name')?.touched">
              <div *ngIf="mainForm.get('full_name')?.invalid && mainForm.get('full_name')?.touched">
                <small class="text-danger" *ngIf="mainForm.get('full_name')?.errors?.['required']">Full name is required</small>
                <small class="text-danger" *ngIf="mainForm.get('full_name')?.errors?.['minlength']">Full name must contain minimum 3 letters</small>
              </div>
            </div>
            <div class="form-group col-12 col-md-4 mt-2">
              <input type="date" class="form-control" formControlName="date_of_birth" placeholder="Date of Birth" [class.is-invalid]="mainForm.get('date_of_birth')?.invalid && mainForm.get('date_of_birth')?.touched">
              <div *ngIf="mainForm.get('date_of_birth')?.invalid && mainForm.get('date_of_birth')?.touched">
                <small class="text-danger" *ngIf="mainForm.get('date_of_birth')?.errors?.['required']">Date of birth is required</small>
              </div>
            </div>
            <div class="form-group col-12 col-md-4 mt-2">
              <input type="email" class="form-control" formControlName="mail" placeholder="Email" [class.is-invalid]="mainForm.get('mail')?.invalid && mainForm.get('mail')?.touched">
              <div *ngIf="mainForm.get('mail')?.invalid && mainForm.get('mail')?.touched">
                <small class="text-danger" *ngIf="mainForm.get('mail')?.errors?.['required']">Email is required</small>
                <small class="text-danger" *ngIf="mainForm.get('mail')?.errors?.['email']">Email is not valid</small>
              </div>
            </div>
          </div>
        </div>

        <!-- Photograph Section -->
        <div class="mt-3">
          <h5 class="mb-2 btn btn-dark">Photograph</h5>
          <div class="row">
            <div class="col-12 col-md-6">
              <div class="input-group">
                <input type="file" class="form-control" (change)="onFileSelected($event)" accept=".jpg,.jpeg" id="photographInput">
                <label class="input-group-text" for="photographInput">Upload</label>
              </div>
            </div>
<!--            <div class="col-12 col-md-6">-->
<!--              <input type="text" class="form-control" [value]="mainForm.get('photograph')?.value" readonly placeholder="No file chosen" [class.is-invalid]="mainForm.get('photograph')?.invalid && mainForm.get('photograph')?.touched">-->
<!--            </div>-->
          </div>
          <small class="text-muted">Please upload a .jpg or .jpeg file.</small>
        </div>

        <!--Organization Photograph Section -->

        <div class="mt-3">
          <h5 id="organization-photograph" class="mb-2 btn btn-dark">Organization Photograph</h5>
          <div class="row">
            <div class="col-12 col-md-6">
              <div class="input-group">
                <input type="file" class="form-control" (change)="onOrganizationFileSelected($event)" accept=".jpg,.jpeg">
                <label class="input-group-text">Upload</label>
              </div>
            </div>
          </div>
          <small class="text-muted">Please upload a .jpg or .jpeg file.</small>
        </div>


        <!-- Additional Details Section -->
        <div class="mt-3">
          <h5 class="mb-2 btn btn-dark">Additional Details</h5>
          <div class="d-flex gap-0 row">
            <!-- <div class="form-group col-12 col-md-4 mt-2">
              <input type="text" class="form-control" formControlName="marital_status" placeholder="Marital Status">
            </div> -->
            <!-- <div class="form-group col-12 col-md-4 mt-2">
              <input type="text" class="form-control" formControlName="hobbies" placeholder="Hobbies">
            </div> -->
            <div class="form-group col-12 col-md-4 mt-2">
              <input type="text" class="form-control" formControlName="dream_sector1" placeholder="Dream Sector 1">
            </div>
            <div class="form-group col-12 col-md-4 mt-2">
              <input type="text" class="form-control" formControlName="dream_sector2" placeholder="Dream Sector 2">
            </div>
            <div class="form-group col-12 col-md-4 mt-2">
              <input type="text" class="form-control" formControlName="career_plans" placeholder="Career Plans">
            </div>
            <div class="form-group col-12 col-md-4 mt-2">
              <input type="text" class="form-control" formControlName="additional_info" placeholder="Additional Info">
            </div>
            <div class="form-group col-12 col-md-4 mt-2">
              <input type="text" class="form-control" formControlName="minor_course_details" placeholder="Minor Course Details">
            </div>
            <div class="form-group col-12 col-md-4 mt-2">
              <input type="text" class="form-control" formControlName="skills" placeholder="Skills">
            </div>
          </div>
        </div>

        <!-- Professional Summary Section -->
        <div class="mt-3">
          <h5 class="mb-2 btn btn-dark">Professional Summary</h5>
          <div class="form-group mt-2">
            <textarea class="form-control" formControlName="prof_summary" placeholder="Professional Summary"></textarea>
          </div>
        </div>

        <!-- Languages Section -->
        <div class="mt-3">
          <h5 class="mb-2 btn btn-dark">Languages</h5>
          <div formArrayName="languages">
            <div *ngFor="let lang of languagesFormArray.controls; let i = index" [formGroupName]="i" class="row mt-2">
              <div class="form-group col-6">
                <input type="text" class="form-control" formControlName="language" placeholder="Language">
              </div>
              <div class="form-group col-6">
                <input type="text" class="form-control" formControlName="proficiency" placeholder="Proficiency">
              </div>
            </div>
          </div>
          <button type="button" class="btn btn-secondary mt-2" (click)="addLanguage()">Add Language</button>
        </div>

        <!-- Education Section -->
        <div class="mt-3">
          <h5 class="mb-2 btn btn-dark">Education</h5>
          <div formArrayName="education">
            <div *ngFor="let edu of educationFormArray.controls; let i = index" [formGroupName]="i" class="row mt-2">
              <div class="form-group col-6 col-md-4">
                <input type="text" class="form-control" formControlName="grade_year" placeholder="Grade Year">
              </div>
              <div class="form-group col-6 col-md-4">
                <input type="number" class="form-control" formControlName="grad_year" placeholder="Graduation Year">
              </div>
              <div class="form-group col-6 col-md-4">
                <input type="text" class="form-control" formControlName="percentage_cgpa" placeholder="Percentage/CGPA">
              </div>
              <div class="form-group col-6 col-md-6">
                <input type="text" class="form-control" formControlName="specialization" placeholder="Specialization">
              </div>
              <div class="form-group col-6 col-md-6">
                <input type="text" class="form-control" formControlName="institution" placeholder="Institution">
              </div>
            </div>
          </div>
          <button type="button" class="btn btn-secondary mt-2" (click)="addEducation()">Add Education</button>
        </div>

  <!-- Extra Curriculars Section -->
  <div class="mt-3">
    <h5 class="mb-2 btn btn-dark">Extra Curriculars</h5>
    <div formArrayName="extra_curriculars">
      <div *ngFor="let activity of extraCurricularsFormArray.controls; let i = index" [formGroupName]="i" class="row mt-2">
        <div class="form-group col-6 col-md-4">
          <input type="text" class="form-control" formControlName="name" placeholder="Name">
        </div>
        <div class="form-group col-6 col-md-4">
          <input type="text" class="form-control" formControlName="category" placeholder="Category">
        </div>
        <div class="form-group col-12">
          <textarea class="form-control" formControlName="description" placeholder="Description"></textarea>
        </div>
      </div>
    </div>
    <button type="button" class="btn btn-secondary mt-2" (click)="addExtraCurricular()">Add Extra Curricular</button>
  </div>

  <!-- Documents Section -->
  <!-- <div class="mt-3">
    <h5 class="mb-2 btn btn-dark">Documents</h5>
    <div formArrayName="documents">
      <div *ngFor="let doc of documentsFormArray.controls; let i = index" [formGroupName]="i" class="row mt-2">
        <div class="form-group col-6 col-md-4">
          <input type="text" class="form-control" formControlName="name" placeholder="Document Name">
        </div>
        <div class="form-group col-6 col-md-4">
          <input type="text" class="form-control" formControlName="category" placeholder="Category">
        </div>
        <div class="form-group col-12">
          <textarea class="form-control" formControlName="description" placeholder="Description"></textarea>
        </div>
      </div>
    </div>
    <button type="button" class="btn btn-secondary mt-2" (click)="addDocument()">Add Document</button>
  </div> -->

  <!-- Projects Section -->
<div class="mt-3">
  <h5 class="mb-2 btn btn-dark">Projects</h5>
  <div formArrayName="projects">
